import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'Angular App';

  change() {
    this.title = 'Angular App 2';
  }
}
<button (click)="change()">Title değiştir</button>

<app-show-productname [productName]="title">
  <h2>data 1</h2>
  <h2>data 2</h2>
</app-show-productname>

<router-outlet></router-outlet>
